import { useNavigation, useRoute } from "@react-navigation/native";
import { LinearGradient } from "expo-linear-gradient";
import { StatusBar } from "expo-status-bar";
import React, { Component, useEffect, useState } from "react";
import {
  Text,
  View,
  ScrollView,
  TouchableOpacity,
  SafeAreaView,
  Dimensions,
  Image,
} from "react-native";
const movieName = "Love at first sight";
import { ChevronLeftIcon, HeartIcon } from "react-native-heroicons/outline";
import {
  fetchMovieCredits,
  fetchMovieDetails,
  fetchPersonDetails,
  fetchSimilarMovies,
  img500,
} from "../api/moviesDb";
import Cast from "./cast";
import MovieList from "./movieList";
const { width, height } = Dimensions.get("screen");
export default function MovieScreen({ route }) {
  const { item } = route.params;
  const [isFavorite, setIsFavorite] = useState(false);
  const [cast, setCast] = useState([]);
  const [similarMovies, setSimilarMovies] = useState([]);
  const [movies, setMovies] = useState({});

  const navigation = useNavigation();
  useEffect(() => {
    getMovieDetails(item.id);
    getMovieCredits(item.id);
    getSimilarMovies(item.id);
  }, [item]);
  const getMovieDetails = async (id) => {
    const data = await fetchMovieDetails(id);
    if (data) setMovies(data);
  };
  const getMovieCredits = async (id) => {
    const data = await fetchMovieCredits(id);
    if (data && data.cast) setCast(data.cast);
  };
  const getSimilarMovies = async (id) => {
    const data = await fetchSimilarMovies(id);
    if (data && data.results) setSimilarMovies(data.results);
  };

  return (
    <ScrollView
      style={{ backgroundColor: "rgb(233, 241, 249)", flex: 1 }}
      contentContainerStyle={{ backgroundColor: "rgb(233, 241, 249)" }}
    >
      <StatusBar translucent={true} />
      <View>
        <SafeAreaView
          style={{
            flexDirection: "row",
            alignItems: "center",
            justifyContent: "space-between",
            paddingHorizontal: 20,
            position: "absolute",
            zIndex: 1111,
            width: "100%",
            top: 40,
          }}
        >
          <TouchableOpacity onPress={() => navigation.goBack()}>
            <ChevronLeftIcon size={22} color="#fff" strokeWidth={2.3} />
          </TouchableOpacity>
          <TouchableOpacity
            onPress={() => {
              setIsFavorite(!isFavorite);
            }}
          >
            <HeartIcon
              size={24}
              color={isFavorite ? "tomato" : "#fff"}
              strokeWidth={2.3}
            />
          </TouchableOpacity>
        </SafeAreaView>
        <View style={{ position: "relative" }}>
          <Image
            // source={require("../../images/img3.jpg")}
            source={{ uri: img500(movies?.poster_path) }}
            style={{
              width,
              height: height * 0.6,
            }}
          />
          <LinearGradient
            colors={[
              "transparent",
              "rgba(233, 241, 249, .5)",
              "rgba(233, 241, 249, 1)",
            ]}
            start={{ x: 0.5, y: 0 }}
            end={{ x: 0.5, y: 1 }}
            style={{ width, height: height * 0.6, position: "absolute" }}
          ></LinearGradient>
        </View>
      </View>
      {/* movie details */}
      <View
        style={{
          alignItems: "center",
          position: "relative",
          // bottom: 100,
        }}
      >
        <Text
          style={{
            fontSize: 30,
            color: "black",
            textAlign: "center",
            width: "90%",
          }}
        >
          {movies?.title}
        </Text>
        {movies?.id ? (
          <Text style={{ fontSize: 12, color: "black", fontWeight: "700" }}>
            {movies?.status} . {movies?.release_date.split("-")[0]} .{" "}
            {movies?.runtime} min
          </Text>
        ) : null}
        <View
          style={{
            width: "40%",
            flexDirection: "row",
            alignItems: "center",
            justifyContent: "space-between",
            marginTop: 10,
          }}
        >
          {/* {movies?.genres?.map((name, index) => {
            return (
              <Text style={{ fontSize: 12, color: "black", fontWeight: "700" }}>
                {genres?.name}
              </Text>
            );
          })} */}
        </View>
        {/* description of the movie */}
        <Text style={{ paddingHorizontal: 14, marginTop: 14 }}>
          {item.overview}
        </Text>
      </View>
      {/* cast by */}
      <Cast cast={cast} />
      {/* similar movies list */}
      <MovieList
        title={"Similar Movies"}
        data={similarMovies}
        hideSeeAll={true}
      />
    </ScrollView>
  );
}
