import { useNavigation } from "@react-navigation/native";
import React, { Component } from "react";
import { Image, ScrollView, Text, TouchableOpacity, View } from "react-native";
import { img185, img500 } from "../api/moviesDb";

export default function Cast({ cast }) {
  const characterName = "John Doe";
  const personName = "John Lee";
  const navigation = useNavigation();
  return (
    <View style={{ marginTop: 20 }}>
      <Text
        style={{
          fontWeight: "800",
          fontSize: 16,
          alignSelf: "flex-start",
          justifyContent: "flex-start",
          marginLeft: 14,
        }}
      >
        Top Cast
      </Text>
      <ScrollView
        style={{ height: 140, width: "100%" }}
        horizontal={true}
        showsHorizontalScrollIndicator={false}
        contentContainerStyle={{
          paddingHorizontal: 14,
        }}
      >
        {cast &&
          cast.map((person, index) => {
            return (
              <TouchableOpacity
                key={index}
                style={{
                  alignItems: "center",
                  marginRight: 12,
                  marginTop: 15,
                }}
                onPress={() => navigation.navigate("PersonScreen", { person })}
              >
                <Image
                  source={{ uri: img185(person?.profile_path) }}
                  // source={person.img}
                  style={{
                    width: 80,
                    height: 80,
                    borderRadius: 100,
                    borderWidth: 2,
                    borderColor: "#ddd",
                  }}
                />
                <Text
                  style={{ fontSize: 14, fontWeight: "800", marginTop: 10 }}
                >
                  {person?.character.length > 10
                    ? person.character.slice(0, 10) + "..."
                    : person.character}
                </Text>
                <Text style={{ fontSize: 12, fontWeight: "700", opacity: 0.5 }}>
                  {person?.name.length > 10
                    ? person.name.slice(0, 10) + "..."
                    : person.name}
                </Text>
              </TouchableOpacity>
            );
          })}
      </ScrollView>
    </View>
  );
}
