import { useNavigation } from "@react-navigation/native";
import React, { Component } from "react";
import {
  Image,
  ScrollView,
  Text,
  TouchableOpacity,
  TouchableWithoutFeedback,
  View,
} from "react-native";
import { img185, img500 } from "../api/moviesDb";
export default function MovieList({ title, data, hideTitle }) {
  const navigation = useNavigation();
  return (
    <View style={{ marginTop: 20 }}>
      {!hideTitle ? (
        <Text
          style={{
            marginLeft: 14,
            fontSize: 18,
            fontWeight: "600",
            marginBottom: 10,
          }}
        >
          {title}
        </Text>
      ) : (
        ""
      )}
      <ScrollView
        contentContainerStyle={{ paddingHorizontal: 12 }}
        horizontal
        showsHorizontalScrollIndicator={false}
      >
        {data.map((item, index) => {
          return (
            <TouchableWithoutFeedback
              style={{ backgroundColor: "red" }}
              key={index}
              onPress={() => navigation.push("Movie", { item })}
            >
              <View
                style={{
                  backgroundColor: "rgb(233, 241, 249)",
                  paddingBottom: 10,
                  marginBottom: 4,
                  elevation: 10,
                  shadowColor: "#fff",
                  marginRight: 10,
                }}
              >
                <Image
                  // source={require("../../images/img1.jpg")}
                  source={{ uri: img185(item.poster_path) }}
                  style={{
                    width: 110,
                    height: 150,
                    borderRadius: 14,
                  }}
                />
                <Text
                  style={{
                    fontWeight: "900",
                    marginTop: 4,
                    textTransform: "capitalize",
                  }}
                >
                  {item.title.length > 16
                    ? item.title.slice(0, 14) + "..."
                    : item.title}
                </Text>
                <View
                  style={{
                    flexDirection: "row",
                    alignItems: "center",
                    opacity: 0.4,
                  }}
                >
                  <Text
                    style={{ fontSize: 8, color: "black", fontWeight: "700" }}
                  >
                    Action .
                  </Text>
                  <Text
                    style={{ fontSize: 8, color: "black", fontWeight: "700" }}
                  >
                    Comedy .
                  </Text>
                  <Text
                    style={{ fontSize: 8, color: "black", fontWeight: "700" }}
                  >
                    Thrill
                  </Text>
                </View>
              </View>
            </TouchableWithoutFeedback>
          );
        })}
      </ScrollView>
    </View>
  );
}
