import axios from "axios";
import { apiKey } from "../../apikey";

const apiBaseUrl = "https://api.themoviedb.org/3";

const trendingMoviesEndpoint = `${apiBaseUrl}/trending/movie/day?api_key=${apiKey}`;
const upcomingMoviesEndpoint = `${apiBaseUrl}/movie/upcoming?api_key=${apiKey}`;
const topRatedMoviesEndpoint = `${apiBaseUrl}/movie/top_rated?api_key=${apiKey}`;

const searchMovieEndpoint = `${apiBaseUrl}/search/movie?api_key=${apiKey}`;

// 'https://api.themoviedb.org/3/search/movie?include_adult=false&language=en-US&page=1'

const movieDetailsEndPoint = (id) =>
  `${apiBaseUrl}/movie/${id}?api_key=${apiKey}`;

const movieCreditsEndPoint = (id) =>
  `${apiBaseUrl}/movie/${id}/credits?api_key=${apiKey}`;

const similarMoviesEndPoint = (id) =>
  `${apiBaseUrl}/movie/${id}/similar?api_key=${apiKey}`;

const personDetailsEndPoint = (id) =>
  `${apiBaseUrl}/person/${id}?api_key=${apiKey}`;

export const img500 = (path) =>
  path ? `https://image.tmdb.org/t/p/w500${path}` : null;
export const img342 = (path) =>
  path ? `https://image.tmdb.org/t/p/w342${path}` : null;
export const img185 = (path) =>
  path ? `https://image.tmdb.org/t/p/w185${path}` : null;

const apiCall = async (endpoint, params) => {
  const options = {
    method: "GET",
    url: endpoint,
    params: params ? params : {},
  };
  try {
    const response = await axios.request(options);
    return response.data;
  } catch (error) {
    console.log("error", error);
    return {};
  }
};
export const fetchTrendingMovies = () => {
  return apiCall(trendingMoviesEndpoint);
};
export const fetchTopRatedMovies = () => {
  return apiCall(topRatedMoviesEndpoint);
};
export const fetchUpComingMovies = () => {
  return apiCall(upcomingMoviesEndpoint);
};

export const fetchMovieDetails = (id) => {
  return apiCall(movieDetailsEndPoint(id));
};

export const fetchMovieCredits = (id) => {
  return apiCall(movieCreditsEndPoint(id));
};

export const fetchSimilarMovies = (id) => {
  return apiCall(similarMoviesEndPoint(id));
};

export const fetchPersonDetails = (id) => {
  return apiCall(personDetailsEndPoint(id));
};
export const searchMovies = (params) => {
  return apiCall(searchMovieEndpoint, params);
};

const apiCalls = async (endpoint, params) => {
  const options = {
    method: "GET",
    url: endpoint,
    params: params ? params : {},
  };
  try {
    const response = await axios.request(options);
    return response.data;
  } catch (error) {
    console.log("error", error);
  }
};
