import React, { useState, useEffect, useRef } from "react";
import {
  StyleSheet,
  Text,
  ScrollView,
  TouchableOpacity,
  Image,
  View,
  SafeAreaView,
  TextInput,
  ActivityIndicator,
  Dimensions,
} from "react-native";
import {
  AntDesign,
  Entypo,
  EvilIcons,
  Feather,
  FontAwesome,
  Ionicons,
} from "@expo/vector-icons";
import { StatusBar } from "expo-status-bar";
import Carousel from "react-native-snap-carousel";
import {
  Bars3CenterLeftIcon,
  MagnifyingGlassIcon,
} from "react-native-heroicons/outline";
import {
  fetchTopRatedMovies,
  fetchTrendingMovies,
  fetchUpComingMovies,
  img500,
} from "../api/moviesDb";
import TrendingMovies from "./trendingMovies";
import MovieList from "./movieList";
const titleColor = "#333";

const { width, height } = Dimensions.get("screen");
export default function HomePage({ navigation }) {
  const [upcoming, setUpComing] = useState([]);
  const [trending, setTrending] = useState([]);
  const [topRated, setTopRated] = useState([]);
  const [searchInput, setSearchInput] = useState("");
  const [open, setOpen] = useState(false);
  useEffect(() => {
    getTrendingMovies();
    getUpcomingMovies();
    getTopRatedMovies();
  }, []);
  const getTrendingMovies = async () => {
    const data = await fetchTrendingMovies();
    if (data && data.results) setTrending(data.results);
    setLoading(false);
  };
  const getUpcomingMovies = async () => {
    const data = await fetchUpComingMovies();
    if (data && data.results) setUpComing(data.results);
    setLoading(false);
  };
  const getTopRatedMovies = async () => {
    const data = await fetchTopRatedMovies();
    if (data && data.results) setTopRated(data.results);
    setLoading(false);
  };
  const [seriesName, setSeriesName] = useState([
    "All",
    "Action",
    "Comedy",
    "Drama",
    "Fantastic",
    "Hollywood",
    "Bollywood",
  ]);
  const [active, setActive] = useState(0);
  const onPress = (index) => {
    setActive(index);
  };
  const onPress1 = () => {
    alert(searchInput);
  };
  const [loading, setLoading] = useState(true);
  return loading ? (
    <View
      style={{ width, height, alignItems: "center", justifyContent: "center" }}
    >
      <ActivityIndicator size={80} animating={true} color="tomato" />
    </View>
  ) : (
    <View style={{ backgroundColor: "rgb(233, 241, 249)" }}>
      <StatusBar
        // translucent={false}
        style={"dark"}
        // backgroundColor="rgb(233, 241, 249)"
      />
      <SafeAreaView style={{ backgroundColor: "rgb(233, 241, 249)" }}>
        <View style={styles.header}>
          <TouchableOpacity style={styles.smallBtn}>
            <Bars3CenterLeftIcon color={titleColor} strokeWidth={2} size={24} />
          </TouchableOpacity>
          <Text style={styles.headerText}>
            <Text style={{ color: "tomato" }}>M</Text>ovies
          </Text>
          <TouchableOpacity
            style={styles.smallBtn}
            onPress={() => {
              setOpen(!open);
              navigation.navigate("Search");
            }}
          >
            <MagnifyingGlassIcon color={titleColor} size={20} />
          </TouchableOpacity>
        </View>
      </SafeAreaView>
      <ScrollView contentContainerStyle={{ paddingBottom: 50 }}>
        <Text
          style={{
            fontSize: 20,
            color: titleColor,
            marginLeft: 14,
            fontWeight: "900",
          }}
        >
          Coming Soon
        </Text>

        {/* Trending movies now */}
        {trending.length > 0 && <TrendingMovies data={trending} />}
        <Text
          style={{
            marginLeft: 14,
            marginTop: 14,
            fontSize: 20,
            fontWeight: "900",
          }}
        >
          Movies
        </Text>
        <ScrollView
          horizontal={true}
          showsHorizontalScrollIndicator={false}
          style={{ marginTop: 10 }}
          contentContainerStyle={{ paddingHorizontal: 14 }}
        >
          {seriesName.map((item, index) => {
            return (
              <TouchableOpacity
                key={index}
                style={{
                  paddingHorizontal: 5,
                  paddingVertical: 5,
                  marginRight: index == seriesName.length - 1 ? 0 : 24,
                }}
                activeOpacity={0.9}
                onPress={() => {
                  onPress(index);
                }}
              >
                <Text
                  style={{
                    fontWeight: "800",
                    fontSize: 14,
                    color: index === active ? "#111" : "#999",
                  }}
                >
                  {item}
                </Text>
              </TouchableOpacity>
            );
          })}
        </ScrollView>
        {/* upcoming MovieList */}
        <MovieList title="Upcoming" hideTitle={true} data={upcoming} />
        {/* topRated MovieList */}
        <MovieList title="Top Rated" data={topRated} />
      </ScrollView>
    </View>
  );
}

const styles = StyleSheet.create({
  header: {
    flexDirection: "row",
    alignItems: "center",
    justifyContent: "space-between",
    paddingHorizontal: 10,
    height: 50,
    marginTop: 40,
  },
  smallBtn: {
    width: 30,
    height: 30,
    alignItems: "center",
    justifyContent: "center",
  },
  headerText: {
    fontSize: 20,
    fontWeight: "900",
    color: titleColor,
  },
  input: {
    flex: 1,
    height: "100%",
    fontSize: 12,
    paddingLeft: 4,
  },
  smallBtn: {
    width: 30,
    height: 30,
    alignItems: "center",
    justifyContent: "center",
  },
});
