import { AntDesign } from "@expo/vector-icons";
import { debounce } from "lodash";
import React, { Component, useCallback, useEffect, useState } from "react";
import {
  Dimensions,
  Image,
  ScrollView,
  StyleSheet,
  Text,
  TouchableWithoutFeedback,
  View,
} from "react-native";
import { Input } from "react-native-elements";

const dummyImage = require("../../images/img3.jpg");

const API_KEY = "590256b2f0175662ad22673a36e5d1b1";
const URL = `https://api.themoviedb.org/3/search/movie?api_key=${API_KEY}`;

const baseImgPath = "https://image.tmdb.org/t/p/w500/";

const { width, height } = Dimensions.get("screen");
const imgWidth = width / 2.4;
const imgHeight = width / 1.8;
export default function DummyFile() {
  useEffect(() => {
    const url = `https://api.themoviedb.org/3/trending/movie/day?api_key=${API_KEY}`;
    const options = {
      method: "GET",
      headers: {
        accept: "application/json",
        Authorization:
          "Bearer eyJhbGciOiJIUzI1NiJ9.eyJhdWQiOiI1OTAyNTZiMmYwMTc1NjYyYWQyMjY3M2EzNmU1ZDFiMSIsInN1YiI6IjYzZDUzYTdkM2RjMzEzMDA4NjRhNjg4ZSIsInNjb3BlcyI6WyJhcGlfcmVhZCJdLCJ2ZXJzaW9uIjoxfQ.OKE7wbOUSo8EBrk1ZOGgoE_SwIYmCYz0shA46nSAZ_g",
      },
    };

    fetch(url, options)
      .then((res) => res.json())
      .then((json) => {
        console.log(json);
        if (json && json.results) {
          setMovies(json.results);
        }
      })
      .catch((err) => console.error("error:" + err));
  }, []);
  const handleSearch = (value) => {
    console.log(value);
  };
  const handleSearchResults = useCallback(debounce(handleSearch, 500));
  const [movies, setMovies] = useState([1, 2, 3, 4]);
  return (
    <View style={styles.container}>
      <Input
        placeholder="Search here"
        style={styles.input}
        onChangeText={handleSearchResults}
        rightIcon={<AntDesign name="search1" size={20} />}
      />
      <ScrollView
        contentContainerStyle={{
          flexDirection: "row",
          alignItems: "center",
          justifyContent: "space-between",
          flexWrap: "wrap",
        }}
      >
        {movies.map((item, index) => {
          return (
            <TouchableWithoutFeedback
              key={index}
              onPress={() => console.log(item?.overview)}
            >
              <Image
                source={{ uri: baseImgPath + item.poster_path }}
                style={{
                  width: imgWidth,
                  height: imgHeight,
                  marginTop: 14,
                  borderRadius: 14,
                }}
              />
            </TouchableWithoutFeedback>
          );
        })}
      </ScrollView>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    paddingHorizontal: 20,
    paddingTop: 30,
  },
});
