import { useNavigation } from "@react-navigation/native";
import React, { Component } from "react";
import {
  Dimensions,
  Image,
  Text,
  TouchableWithoutFeedback,
  View,
} from "react-native";
import Carousel from "react-native-snap-carousel";
import { img500 } from "../api/moviesDb";
const { width, height } = Dimensions.get("screen");
export default function TrendingMovies({ data }) {
  const navigation = useNavigation();
  const handleClick = (item) => {
    navigation.navigate("Movie", { item });
    console.log(item);
  };
  return (
    <View style={{ marginTop: 10 }}>
      <Carousel
        data={data}
        loop={true}
        zoomScale={0}
        autoplay={true}
        autoplayInterval={3000}
        renderItem={({ item }) => (
          <MovieCard item={item} handleClick={() => handleClick(item)} />
        )}
        sliderWidth={width}
        itemWidth={width * 0.92}
        firstItem={1}
        slideStyle={{
          display: "flex",
          alignItems: "center",
        }}
      ></Carousel>
    </View>
  );
}

const movieName = "Sonic the hedgehog";
const relaseDate = "Feb 12, 2020";

const MovieCard = ({ item, handleClick }) => {
  return (
    <TouchableWithoutFeedback onPress={() => handleClick(item)}>
      <View style={{ position: "relative" }}>
        <Image
          // source={require("../../images/dummyImg.jpg")}
          source={{ uri: img500(item.poster_path) }}
          style={{
            width: width * 0.92,
            height: height * 0.26,
            borderRadius: 20,
            resizeMode: "contain",
          }}
        />
        <View
          style={{
            position: "absolute",
            bottom: 10,
            left: 20,
          }}
        >
          <Text
            style={{
              fontSize: 10,
              fontWeight: "700",
              color: "#ddd",
            }}
          >
            Release
          </Text>
          <Text
            style={{
              fontSize: 10,
              fontWeight: "700",
              color: "#ddd",
            }}
          >
            {item.release_date ? item.release_date : ""}
          </Text>
        </View>
      </View>
    </TouchableWithoutFeedback>
  );
};
